INSERT INTO `books` VALUES (83, '[\"1\",\"14\",\"9\",\"18\"]', 'xteam', 'Tung Tiền Hữu Tọa Linh Kiếm Sơn', 'tung-tien-huu-toa-linh-kiem-son', 'Tung Tiền Hữu Tọa Linh Kiếm Sơn', NULL, '<p><strong>Tung Tiền Hữu Tọa Linh Kiếm Sơn</strong>:</p>\r\n\r\n<p>Th&aacute;i cổ thần vẫn, hằng cổ nghiền n&aacute;t, mạt ph&aacute;p bắt đầu, kỳ t&agrave;i bu&ocirc;ng xuống...&nbsp;<br />\r\nMột g&atilde; FA từ thế giới hiện đại xuy&ecirc;n việt qua vương giả đại lục, sở hữu Kh&ocirc;ng Linh Căn ng&agrave;n năm hiếm gặp, hắn b&aacute;i nhập sơn m&ocirc;n linh kiếm ph&aacute;i, với tr&iacute; th&ocirc;ng minh nghiền &aacute;p dị giới, bước n&ecirc;n con đường trở th&agrave;nh v&ocirc; thượng cường giả...</p>', 2, 'hiepnguyen', 2, 0, 4, 1, 1, '2018-06-04 17:23:21', '2018-06-04 17:23:21', NULL);
INSERT INTO `books` VALUES (84, '[\"1\",\"14\",\"18\",\"3\"]', 'xteam', 'Tuyết Sơn Phi Hồ', 'tuyet-son-phi-ho', NULL, NULL, '<p><strong>Tuyết Sơn Phi Hồ</strong>:</p>\r\n\r\n<p>Sấm Vương ph&aacute; kinh, đại tướng triều Minh đều đầu h&agrave;ng, d&acirc;ng l&ecirc;n t&agrave;i bảo để chuộc mạng, v&agrave;ng bạc chất th&agrave;nh n&uacute;i. Sấm Vương sau khi rời khỏi kinh ph&aacute;i th&acirc;n t&iacute;n đến quan ngoại t&igrave;m chỗ giấu t&agrave;i bảo, để chuẩn bị cho sau n&agrave;y.</p>\r\n\r\n<p>Trường Bạch Sơn những năm C&agrave;n Long, ba lộ nh&acirc;n m&atilde; triển khai hỗn chiến tranh đoạt bảo hạp. Trong bảo hạp kh&ocirc;ng những li&ecirc;n quan kho b&aacute;u Sấm Vương, c&ograve;n li&ecirc;n quan bốn hộ vệ Sấm Vương v&agrave; &acirc;n o&aacute;n d&acirc;y dưa qua c&aacute;c đời.&nbsp;</p>', 2, 'hiepnguyen', 2, 0, 4, 1, 1, '2018-06-04 17:24:45', '2018-06-04 17:24:45', NULL);
INSERT INTO `books` VALUES (87, '[\"14\",\"9\",\"18\",\"3\",\"15\"]', 'xteam', 'Adarushan no Hanayome', 'adarushan-no-hanayome', NULL, NULL, '<p><strong>Adarushan no Hanayome</strong>:</p>\r\n\r\n<p>Nước Adarshan bị chinh phục bởi đế chế Kastoria. Để thắt chặt t&igrave;nh li&ecirc;n minh giữa hai nước v&agrave; chấm dứt chiến tranh, Alexid Deke Adars (ho&agrave;ng tử của nuớc Adarshan) đ&atilde; bị rằng buộc v&agrave;o một cuộc h&ocirc;n nh&acirc;n ch&iacute;nh trị với c&ocirc;ng ch&uacute;a thứ 6 của triều đ&igrave;nh Kastoria - Justinia. Nhưng c&ocirc; ấy chỉ 10 tuổi ...! Liệu cuộc h&ocirc;n nh&acirc;n giữa Alexid v&agrave; c&ocirc; c&ocirc;ng ch&uacute;a 10 tuổi sẽ diễn ra như thế n&agrave;o?</p>', 2, 'kakaa', 2, 0, 4, 1, 1, '2018-06-04 17:29:11', '2018-06-04 17:41:12', NULL);
